function [words] = find_closest_words(word, model, topk)  word_embedding_weights = model.word_embedding_weights;  vocab = model.vocab;  id1 = strmatch(word, vocab, 'exact');  if ~any(id1)    fprintf(1, 'Word ''%s\'' not in vocabulary.\n', word1);    return;  end  if(size(vocab, 2) < topk)    fprintf(1, 'Vocab size is only %d, so cannot return top %d words', size(vocab,2), topk);    return;  end  word_rep1 = word_embedding_weights(id1, :);  diff = word_embedding_weights - word_rep1;  % Broadcasting  distance = sqrt(sum(diff .^ 2, 2));  [sorted_dist, indices] = sort(distance);  words = vocab(indices(2:topk+1));end